import { Component, OnInit } from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {ActivatedRoute, Router} from "@angular/router";

@Component({
  selector: 'app-bills',
  templateUrl: './bills.component.html',
  styleUrls: ['./bills.component.css']
})
export class BillComponent implements OnInit {
  bill : any;
  customerId !:number;
  total!:number;
  constructor(private  http:HttpClient , private  router : Router,private route :ActivatedRoute) {
    this.customerId=route.snapshot.params['customerId']

  }

  ngOnInit(): void {
    // this.http.get("http://localhost:8888/BILLING-SERVICE/fullBill/"+this.customerId).subscribe(
    this.http.get("http://localhost:8888/BILLING-SERVICE/fullbill/"+this.customerId).subscribe(
      { next:(data)=>{
          this.bill = data;
          this.total=0
          for (let i = 0; i < this.bill.productItems.length; i++) {
            this.total = this.total + this.bill.productItems[i].quantity*this.bill.productItems[i].prix
          }
          this.bill.productItems
        },
        error : (err)=>{}
      });
  }


}

// http://localhost:8083/bills/1/productItems
<div class="container" *ngIf="bill">
  <br>
  <div clas="">
    <div class="col-md-12">
      <div class="card">
        <b><div class="card-header"> Bill ID:{{bill.id}}</div></b>
        <div class="card-body text-sm-start">
          <ul class="list-group">
            <li class="list-group-item">Billing Date: {{bill.billingDate | date:'dd/MM/yyyy'}}</li>
            <li class="list-group-item">Total: {{total}} MAD</li>
            <li class="list-group-item">Customer Id: {{bill.customer.id}}</li>
            <li class="list-group-item">name: {{bill.customer.name}}</li>
            <li class="list-group-item">Email: {{bill.customer.email}}</li>
          </ul>
        </div>
      </div></div>
    <br>
    <div class="col-md-12">
      <div class="card">
        <b><div class="card-header">Product Items</div></b>
        <div class="card-body">
          <table class="table table-sm">
            <thead>
            <tr>
              <th> Product Id</th><th>Product Name</th><th>Quantity</th><th>Price (MAD)</th>
            </tr>
            </thead>
            <tbody>
            <tr *ngFor="let pi of bill.productItems">
              <td>{{pi.id}}</td>
              <td>{{pi.productName}}</td>
              <td>{{pi.quantity}}</td>
              <td>{{pi.prix}}</td>
            </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  </div>
</div>
