import { Component, OnInit } from '@angular/core';
import {HttpClient} from "@angular/common/http";

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css']
})
export class ProductsComponent implements OnInit {
  products : any;
  constructor(private  http:HttpClient) { }

  ngOnInit(): void {
    this.http.get("https://localhost:8888/PRODUCT-SERVICE/products?projection=fullProduct").subscribe(
      { next:(data)=>{
          this.products = data;
        },
        error : (err)=>{}
      });
  }}
<div class="container">
  <table class ="table" *ngIf="products">
    <thead>
    <br>
    <tr>
      <th>ID</th><th>Name</th><th>Price</th><th>Quantity</th>
    </tr>
    </thead>
    <tbody>
    <tr *ngFor ="let p of products._embedded.products">
      <td> {{p.id}}</td>
      <td> {{p.name}}</td>
      <td> {{p.price}}</td>
      <td> {{p.quantity}}</td>

    </tr>
    </tbody>
  </table>

</div>
