import { Component, OnInit } from '@angular/core';
import {HttpClient} from "@angular/common/http";

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css']
})
export class ProductsComponent implements OnInit {
  products : any;
  constructor(private  http:HttpClient) { }

  ngOnInit(): void {
    this.http.get("http://localhost:8888/PRODUCT-SERVICE/products").subscribe(
      { next:(data)=>{
          this.products = data;
        },
        error : (err)=>{}
      });
  }}
<div class="container" *ngIf="products">
  <form [formGroup]="searchProductsForm" (ngSubmit)="searchProducts()">
    <div class="input-group input-group-sm">
      <label class="input-group-text">Keyword</label>
      <input class="form-control" type="text" formControlName="keyword">
      <button class="btn btn-success">Search</button>
    </div>
  </form>
  <form [formGroup]="productFormGroup" (ngSubmit)="addProduct()">
    <table class="table" >
      <thead>
      <tr>
        <th>ID</th><th>Name</th><th>Price</th><th>Quantity</th>
      </tr>
      <tr>
        <td></td>
        <td><input type="text" formControlName="name"></td>
        <td><input type="text" formControlName="price"></td>
        <td><input type="text" formControlName="quantity"></td>
        <td>
          <button class="btn btn-sm btn-success">Add</button>
        </td>
      </tr>
      </thead>
      <tbody>
      <tr *ngFor="let p of products._embedded.products">
        <td>{{p.id}}</td>
        <td>{{p.name}}</td>
        <td>{{p.price}}</td>
        <td>{{p.quantity}}</td>
      </tr>
      </tbody>
    </table>
  </form>
</div>
